import React, { useState } from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Divider from '@mui/material/Divider';
import Drawer from '@mui/material/Drawer';
import IconButton from '@mui/material/IconButton';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';


import Cards from './Cards';
import data from './test.json';



const drawerWidth = 300;
var balance = 0;
var arr = [];
const Navbar = (props) => {

    var [totalBalance, setTotalBalance] = useState(balance);
    var [cart,setCart] = useState(arr);


    const { window } = props;
    const [mobileOpen, setMobileOpen] = React.useState(false);
    const navItems = ['Break Your Fast', 'Time for Lunch', 'Can I have Snacks', 'Dinner', 'Burger and Beverages', 'More..'];

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen);
    };
    const addToCart = (e) => {
        console.log(e.target);
    }
    const popUp = () => {

    }


    const drawer = (
        <Box onClick={handleDrawerToggle} sx={{ textAlign: 'center' }}>
            <Typography variant="h4" sx={{ my: 2 }}>
                Order Now
            </Typography>
            <Divider />
            <List>
                {navItems.map((item) => (
                    <ListItem key={item} disablePadding>
                        <ListItemButton sx={{ textAlign: 'start' }}>
                            <ListItemText primary={item} />
                        </ListItemButton>
                    </ListItem>
                ))}
            </List>
        </Box>
    );





    const container = window !== undefined ? () => window().document.body : undefined;
    return (
        <>
            <Box sx={{}}>
                <AppBar component="nav" sx={{ backgroundColor: "#eee" }} >
                    <Toolbar>
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            edge="start"
                            onClick={handleDrawerToggle}
                            sx={{ mr: 2, display: { md: 'none' } }}
                        >
                            <MenuIcon />
                        </IconButton>

                        <Box sx={{ display: { xs: 'none', sm: 'none', md: 'block', margin: "auto", width: "75%" } }}>
                            {navItems.map((item) => (
                                <Button key={item} sx={{ color: '#000', textAlign: "center", marginLeft: "3vh", fontSize: "2.5vh" }}>
                                    {item}
                                </Button>
                            ))}
                        </Box>
                    </Toolbar>
                </AppBar>
                <Box component="nav">
                    <Drawer
                        container={container}
                        variant="temporary"
                        open={mobileOpen}
                        onClose={handleDrawerToggle}
                        ModalProps={{
                            keepMounted: true, // Better open performance on mobile.
                        }}
                        sx={{
                            display: { sm: 'block', md: 'none' },
                            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
                        }}
                    >
                        {drawer}
                    </Drawer>
                </Box>
                {/* <div id="cardDisplay" onClick={addToCart}>
                    {data.map((val) => {
                        return <Cards id={val.id} name={val.name} src={val.photograph} type={val.type} price={val.price} />
                    })}
                </div> */}
                <div id='cartDisplay'>
                    <div>
                        <div id="pop">
                            <i class="fa-solid fa-angles-up" style={{ fontSize: "4vh", color: "red", padding: "1vh 2vh", border: "1px solid black", cursor: "pointer", marginLeft: "4vh" }} onClick={popUp} />
                        </div>
                        <div id='details'>

                            <h1></h1>

                        </div>
                    </div>
                    <div id='cartList'>

                    </div>
                </div>

            </Box>
        </>
    )
}

export default Navbar
